    <script id="vertexShader" type="x-shader/x-vertex">
        void main() {
            gl_Position = vec4( position, 1.0 );
        }
    </script>
    <script id="fragmentShader" type="x-shader/x-fragment">
         #ifdef GL_ES
        precision mediump float;
        #endif

        #define PI 3.14159265359

        uniform vec2 u_resolution;
        uniform vec2 u_mouse;
        uniform float u_time;


        /**
         * Draw a circle at vec2 `pos` with radius `rad` and
         * color `color`.
         */
        vec4 circleA(vec2 uv, vec2 pos, float rad, vec3 color) {
            float d = length(pos - uv) - rad;
            float t = clamp(d, 0.0, 1.0);
            return vec4(color, 1.0 - t);
        }

        vec4 circleWithBackWithoutCurves(){
        	vec2 uv = gl_FragCoord.xy;
            vec2 center = u_resolution.xy *(u_mouse.xy/1000.0);
            float radius = u_resolution.y;

            // Background layer color azul fondo
            vec4 layer1 = vec4(0.1843,0.298,0.7647, 1.0);
            
            // Circle
            vec3 azul = vec3(0.0156,0.1137,0.2352);
            vec4 layer2 = circleA(gl_FragCoord.xy, center, radius, azul);

            // Blend the two
            return mix(layer1,layer2,layer2.a);
    	}

    	vec4 circleB(vec2 uv,vec2 pos, float rad, vec3 color){
            float l = length(uv);
            float thi = atan(uv.y,uv.x);
            float d = length(pos - uv) - rad;
            float t = clamp(d,0.0,1.0);

            //float r = 1.0 + 0.3 * sin(5.0 *thi)*pow(sin(u_mouse.x * 0.01),1.0);
            

            float r = 1.0 + 0.3 * sin(5.0 *thi)*pow(sin(u_mouse.x * 0.01),3.0);
            
            l = step(l,r);
            vec3 col = color;
            return vec4(col*l,1.0);
    	}

    	vec4 circleWithAlteration(){
    		vec4 layer1 = vec4(0.1843,0.298,0.7647, 1.0);

	    	vec2 uv = gl_FragCoord.xy / u_resolution.xy;
	    	vec2 center = u_resolution.xy *(u_mouse.xy/1000.0);

            float radius = u_resolution.y * u_mouse.x*0.01;
            
            vec3 col = vec3(0.0156,0.1137,0.2352);

            vec4 layer2 = circleB(uv,center,radius,col);
            return mix(layer1,layer2,layer2.b);
	    }


        void main(){
            //gl_FragColor = circleWithBackWithoutCurves();
            gl_FragColor = circleWithAlteration();
        }

    </script>
    <script>
        const container = document.getElementById( 'canvas' );
        let camera, scene, renderer;
        let uniforms;
        init = () => {
        	scene = new THREE.Scene();
            camera = new THREE.Camera();
            camera.position.z = 1;

            renderer = new THREE.WebGLRenderer();
            renderer.setPixelRatio( window.devicePixelRatio );
            container.appendChild( renderer.domElement );

            var geometry = new THREE.PlaneBufferGeometry( 2, 2 );

            uniforms = {
                u_time: { type: "f", value: 1.0 },
                u_resolution: { type: "v2", value: new THREE.Vector2() },
                u_mouse: { type: "v2", value: new THREE.Vector2() }
            };

            var material = new THREE.ShaderMaterial( {
                uniforms: uniforms,
                vertexShader: document.getElementById( 'vertexShader' ).textContent,
                fragmentShader: document.getElementById( 'fragmentShader' ).textContent
            } );

            var mesh = new THREE.Mesh( geometry, material );
            scene.add( mesh );
            onWindowResize();
            window.addEventListener( 'resize', onWindowResize, false );

            document.onmousemove = (e) =>{
              uniforms.u_mouse.value.x = e.pageX
              uniforms.u_mouse.value.y = e.pageY
            }
        }

        onWindowResize = ( event ) => {
            renderer.setSize( container.offsetWidth, container.offsetHeight );
            uniforms.u_resolution.value.x = renderer.domElement.width;
            uniforms.u_resolution.value.y = renderer.domElement.height;
        }

        animate = () => {
            requestAnimationFrame( animate );
            render();
        }

        render = () => {
            uniforms.u_time.value += 0.05;
            renderer.render( scene, camera );
        }

        init();
        animate();
    </script>
